import React, { Fragment, useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import axios from "axios";
import { Name } from "ajv";
const StudentCurd = () => {
  const [data, Setdata] = useState();
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [name, SetName] = useState("");
  const [Age, SetAge] = useState("");
  const [Address, SetAddress] = useState("");
  const [Class, SetClass] = useState("");

  
  const [EditId, SetEditId] = useState("");
  const [Editname, SetEditName] = useState("");
  const [EditAge, SetEditAge] = useState("");
  const [EditAddress, SetEditAddress] = useState("");
  const [EditClass, SetEditClass] = useState("");

 

  console.log("set data:", data);

  const rows = [
    {
      Id: 1,
      Name: "Swara",
      age: 23,
      Address: "pune",
      Class: "second",
    
    },
    {
      Id: 2,
      Name: "Sunita",
      age: 23,
      Address: "pune",
      Class: "second",
     
    },
    {
      Id: 3,
      Name: "Mira",
      age: 23,
      Address: "pune",
      Class: "second",
   
    },
  ];
  const HandleEdit = (Id) => {
    // if (window.confirm("Edit Your message...") === true) {
    //   alert(Id);
    // }
    handleShow();
  };
  const handleUpdate = (Id) => {
    // if (window.confirm("Edit Your message...") === true) {
    //   alert(Id);
    // }
    handleShow();
  };

  const HandleDelete = (Id) => {
    // if (window.confirm("Are You sure to delete message...") === true) {
    //   alert(Id);
    // }
    handleShow();
  };

  useEffect(() => {
    Getdata();
  }, []);

  const Getdata=(id)=>{
   axios.get("http://localhost:5151/student/index")
  .then((result)=>{
    Setdata(result.data)
  }).catch((error)=>{
    console.log(error)
  })
  }
  return (
    <Fragment>
      <br></br>
        <Container>
            <Row>
              <Col lg={6} md={6}>
                <input
                  type="Text"
                  className="form-control"
                  placeholder="Name"
                  value={Name}
                  onChange={(e)=>SetName(e.target.value)}
                />
              </Col>
              <Col lg={6} md={6}>
                <input
                  type="Text"
                  className="form-control"
                  placeholder="Address"
                  value={Address}
                  onChange={(e)=>SetAddress(e.target.value)}
                />
              </Col>
            </Row>
            <br></br>
            <Row>
              <Col lg={6} md={6}>
                <input
                  type="Text"
                  className="form-control"
                  placeholder="Age"
                  value={Age}
                  onChange={(e)=>SetAge(e.target.value)}
                />
              </Col>
              <Col lg={6} md={6}>
                <input
                  type="Text"
                  className="form-control"
                  placeholder="Class"
                  value={Class}
                  onChange={(e)=>SetClass(e.target.value)}
                />
              </Col>
              
            </Row>
          </Container><br></br>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Age</th>
            <th>Address</th>
            <th>Class</th>
            
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rows && rows.length > 0
            ? rows.map((item, index) => {
                return (
                  <>
                    <tr key={index}>
                      <td>{index + 1}</td>
                      <td>{item.Name}</td>
                      <td>{item.age}</td>
                      <td>{item.Address}</td>
                      <td>{item.Class}</td>
                     
                      <td colSpan={2}>
                        <button
                          className="btn btn-primary"
                          onClick={() => HandleEdit(item.Id)}
                        >
                          Edit
                        </button>{" "}
                        &nbsp;
                        <button
                          className="btn btn-danger"
                          onClick={() => HandleDelete(item.Id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  </>
                );
              })
            : "...Loading"}
        </tbody>
      </Table>
      <Modal show={show} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Update/Edit Employee</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          
            <Row>
              <Col xs={10} md={6}>
                <input
                  type="Text"
                  className="form-control"
                  placeholder="Name"
                  value={Name}
                  onChange={(e)=>SetEditName(e.target.value)}
                />
              </Col>
              <Col xs={10} md={6}>
                <input
                  type="Text"
                  className="form-control"
                  placeholder="Address"
                  value={Address}
                  onChange={(e)=>SetEditAddress(e.target.value)}
                />
              </Col>
            </Row>
            <br></br>
            <Row>
              <Col  xs={10} md={6}>
                <input
                  type="Text"
                  className="form-control"
                  placeholder="Age"
                  value={Age}
                  onChange={(e)=>SetEditAge(e.target.value)}
                />
              </Col>
              <Col  xs={10} md={6}>
                <input
                  type="Text"
                  className="form-control"
                  placeholder="Class"
                  value={Class}
                  onChange={(e)=>SetEditClass(e.target.value)}
                />
              </Col>
             
            </Row>
         
          <br></br>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleUpdate}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    </Fragment>
  );
};

export default StudentCurd;
