import React, { Fragment, useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
const StudentCurd = () => {
  const [Data, Setdata] = useState(false);
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);


  const [name,SetName]=useState('')
  const [Age,SetAge]=useState('')
  const [Address,SetAddress]=useState('')
  const [Class,SetClass]=useState('')
  
  const [SkillId,SetSkillId]=useState('')


  const [Editname,SetEditName]=useState('')
  const [EditAge,SetEditAge]=useState('')
  const [EditAddress,SetEditAddress]=useState('')
  const [EditClass,SetEditClass]=useState('')
  
  const [EditSkillId,SetEditSkillId]=useState('')

  console.log("set data:", Data);

  const rows = [
    {
      Id: 1,
      Name: "Swara",
      age: 23,
      Address: "pune",
      Class: "second",
      SkillId: 1,
    },
    {
      Id: 2,
      Name: "Sunita",
      age: 23,
      Address: "pune",
      Class: "second",
      SkillId: 4,
    },
    {
      Id: 3,
      Name: "Mira",
      age: 23,
      Address: "pune",
      Class: "second",
      SkillId: 5,
    },
  ];
  const HandleEdit = (Id) => {
    // if (window.confirm("Edit Your message...") === true) {
    //   alert(Id);
    // }
    handleShow();
  };
  const handleUpdate = (Id) => {
    // if (window.confirm("Edit Your message...") === true) {
    //   alert(Id);
    // }
    handleShow();
  };
  
  const HandleDelete = (Id) => {
    // if (window.confirm("Are You sure to delete message...") === true) {
    //   alert(Id);
    // }
    handleShow();
  };

  useEffect(() => {
    Setdata(rows);
  }, []);
  return (
    <Fragment>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Age</th>
            <th>Address</th>
            <th>Class</th>
            <th>SkillId</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rows && rows.length > 0
            ? rows.map((item, index) => {
                return (
                  <>
                    <tr key={index}>
                      <td>{index + 1}</td>
                      <td>{item.Name}</td>
                      <td>{item.age}</td>
                      <td>{item.Address}</td>
                      <td>{item.Class}</td>
                      <td>{item.SkillId}</td>
                      <td colSpan={2}>
                        <button
                          className="btn btn-primary"
                          onClick={()=>HandleEdit(item.Id)}
                        >
                          Edit
                        </button>{" "}
                        &nbsp;
                        <button
                          className="btn btn-danger"
                          onClick={()=>HandleDelete(item.Id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  </>
                );
              })
            : "...Loading"}
        </tbody>
      </Table>
      <Modal show={show} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Update/Edit Employee</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Container>
          <Row>
            <Col xs={10} md={6}>
              <input type="Text" className="form-control" placeholder="Name" />
            </Col>
            <Col xs={10} md={6}>
            <input type="Text" className="form-control" placeholder="Address" />
            </Col>
            </Row><br></br>
            <Row>
            <Col xs={6} md={4}>
            <input type="Text" className="form-control" placeholder="Age" />
            </Col>
            <Col xs={6} md={4}>
            <input type="Text" className="form-control" placeholder="Class" />
            </Col>
            <Col xs={6} md={4}>
            <input type="Text" className="form-control" placeholder="SkillId" />
            </Col></Row>
        </Container>
        <br></br>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleUpdate}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </Fragment>
  );
};

export default StudentCurd;
