import React, { useState } from 'react';

import { Link } from 'react-router-dom';
import Validation from './Loginvalidation';

const Login = () => {
    const [values, setValue] = useState({
        email: '',
        password: '',
    });
    const [errors, setErrors] = useState({});
    const handleSubmit = async (event) => {
        event.preventDefault();
        setErrors(Validation(values));
    };

    const handleInput = (event) => {
        setValue(prev => ({
            ...prev,
            [event.target.name]: [event.target.value]
        }));
    };
    console.log('Login page open...');
    return (
        <>
            <div className='d-flex justify-content-center align-items-center p-3 border bg-info vh-100'>
                <div className='bg-white p-3 rounded w-2'>
                    <form action="" onSubmit={handleSubmit}>
                        <h3>
                            <strong>Login Page</strong>
                        </h3>
                        <div className='mb-3'>
                            <label htmlFor='Name' className='col-lg-2 col-form-label'>
                                <strong>Email:</strong>
                            </label>
                            <input
                                name='email'
                                type='text'
                                onChange={handleInput}
                                placeholder='Enter email'
                                className='form-control rounded-2'
                            />
                            {errors.email && (
                                <span className='text-danger'>{errors.email}</span>
                            )}
                        </div>

                        <div className='mb-3'>
                            <label htmlFor='Password' className='col-lg-2 col-form-label'>
                                <strong>Password:</strong>
                            </label>
                            <input
                                name='password'
                                type='password'
                                onChange={handleInput}
                                placeholder='Enter Password'
                                className='form-control rounded-2 '
                                id='inputPassword'
                            />{' '}
                            {errors.password && (
                                <span className='text-danger'>{errors.password}</span>
                            )}
                        </div>
                        <button type='Submit' className='btn btn-success w-100 rounded-2 '>
                            <strong>Sign in</strong>
                        </button>
                        <p>You are agree to our terms and conditions..</p>
                        <Link
                            to='/signup'
                            className='btn btn-default border w-100 bg-light text-decoration-none rounded-2 '
                        >
                            <strong>Create Account</strong>
                        </Link>
                    </form>
                </div>
            </div>
        </>
    );
};

export default Login;
